examples.code:
import { createApp, json } from 'isahc';

const app = createApp();
app.use(json());

const users = [];

// Get all users
app.get('/users', (ctx) => {
  ctx.json(users);
});

// Get user by ID
app.get('/users/:id', (ctx) => {
  const user = users.find(u => u.id === ctx.params.id);
  if (!user) {
    ctx.status(404).json({ error: 'User not found' });
    return;
  }
  ctx.json(user);
});

// Create user
app.post('/users', (ctx) => {
  const user = {
    id: Date.now().toString(),
    ...ctx.request.body
  };
  users.push(user);
  ctx.status(201).json(user);
});

// Update user
app.put('/users/:id', (ctx) => {
  const index = users.findIndex(u => u.id === ctx.params.id);
  if (index === -1) {
    ctx.status(404).json({ error: 'User not found' });
    return;
  }
  users[index] = { ...users[index], ...ctx.request.body };
  ctx.json(users[index]);
});

// Delete user
app.delete('/users/:id', (ctx) => {
  const index = users.findIndex(u => u.id === ctx.params.id);
  if (index === -1) {
    ctx.status(404).json({ error: 'User not found' });
    return;
  }
  users.splice(index, 1);
  ctx.status(204).send();
});

app.listen(3000);

import { createApp, staticFiles } from 'isahc';
import path from 'path';

const app = createApp();

// Serve static files from public directory
app.use(staticFiles(path.join(process.cwd(), 'public'), {
  index: 'index.html',
  dotfiles: 'ignore'
}));

// API routes
app.get('/api/health', (ctx) => {
  ctx.json({ status: 'ok', timestamp: new Date().toISOString() });
});

app.listen(3000, '0.0.0.0');

import { createClient, get, post } from 'isahc';

// Using global functions
async function fetchData() {
  try {
    const response = await get('https://jsonplaceholder.typicode.com/posts/1');
    console.log('Post:', response.data);

    const newPost = await post('https://jsonplaceholder.typicode.com/posts', {
      title: 'My New Post',
      body: 'This is the post content',
      userId: 1
    });
    console.log('Created:', newPost.data);
  } catch (error) {
    console.error('Error:', error);
  }
}

// Using client instance
const client = createClient('https://api.example.com');
client.setHeader('Authorization', 'Bearer token');
client.setTimeout(5000);

async function apiCall() {
  try {
    const users = await client.get('/users');
    console.log('Users:', users.data);

    const user = await client.post('/users', {
      name: 'John Doe',
      email: 'john@example.com'
    });
    console.log('Created user:', user.data);
  } catch (error) {
    console.error('API Error:', error);
  }
}

fetchData();
apiCall();

import { createApp, json } from 'isahc';

const app = createApp();
app.use(json());

// Custom authentication middleware
const authMiddleware = (ctx, next) => {
  const token = ctx.request.headers.authorization;

  if (!token || !token.startsWith('Bearer ')) {
    ctx.status(401).json({ error: 'Unauthorized' });
    return;
  }

  // Verify token (simplified)
  const actualToken = token.slice(7);
  if (actualToken !== 'valid-token') {
    ctx.status(401).json({ error: 'Invalid token' });
    return;
  }

  // Add user info to context
  ctx.user = { id: 1, name: 'John Doe' };
  return next();
};

// Logging middleware
const logMiddleware = (ctx, next) => {
  const start = Date.now();
  console.log(`${ctx.request.method} ${ctx.request.url}`);

  const result = next();

  const duration = Date.now() - start;
  console.log(`Completed in ${duration}ms`);

  return result;
};

// Apply middleware
app.use(logMiddleware);

// Public routes
app.get('/public', (ctx) => {
  ctx.json({ message: 'This is public' });
});

// Protected routes
app.use(authMiddleware);
app.get('/protected', (ctx) => {
  ctx.json({ 
    message: 'This is protected',
    user: ctx.user 
  });
});

app.listen(3000);
